import React, { Component } from "react";
import ShowMyList from "./showMyList";

class AddMyList extends Component {
    constructor(props) {
        super(props);
        this.list = {
            items: []
        };
        this.addItem = this.addItem.bind(this);
        // This binding is necessary to make `this` work in the callback
    }
    addItem(e) {
        if (this.myinput.value !== "") {
            var item = {
                value:this.myinput.value
            };
            this.items = this.list.items.push(this.item);
        }
        this.myinput.value = "";
        console.log(this.list);
        e.preventDefault();
    }
    render() {
        return (
            <div>
                <form onSubmit={this.addItem}>
                    <input ref={(a) => this.myinput = a}>
                    </input>
                    <button type="submit">Add</button>
                </form>
                <ShowMyList data={this.list.items} />
            </div>
        );
    }
}
export default AddMyList;
